import React from "react";
import { EventCard } from "./EventCard";
import { useFetch } from "../../../hooks/useFetch";

export const CardGrid = () => {
  let { data, loading, error } = useFetch("http://localhost:5000/games");
  data = [
    {
      _id: "YtDjXlb2V9",
      home_team: "FC Bayern",
      away_team: "Manchester City",
      date: "2022-12-14T10:30:00",
      stadium: "Allianz Arena",
    },
    {
      _id: "kKHk5ZwJ72",
      home_team: "RB Leipzig",
      away_team: "Borussia Dortmund",
      date: "2023-01-30T14:30:00",
      stadium: "Red Bull Arena",
    },
    {
      _id: "W-0w4nJlvv",
      home_team: "SC Freiburg",
      away_team: "FSV Mainz 05",
      date: "2022-02-14T15:00:00",
      stadium: "Europa-Park-Stadion",
    },
    {
      _id: "AnhAtPLMPY",
      home_team: "SC Freiburg",
      away_team: "FSV Mainz 05",
      date: "2022-02-14T15:00:00",
      stadium: "Europa-Park-Stadion",
    },
  ];
  return (
    <div className="container">
      <div className="row ">
        {loading && <div>loading</div>}
        {data &&
          data.slice(0, 4).map(
            (
              e,
              i //erste 3
            ) => (
              <div
                className="col col-md-4 d-flex justify-content-center my-2"
                key={i}
              >
                <EventCard
                  home_team={e.home_team}
                  away_team={e.away_team}
                  date={e.date}
                  stadium={e.stadium}
                />
              </div>
            )
          )}
      </div>
    </div>
  );
};
