import React, { useState } from "react";
import { useFetch } from "../../../hooks/useFetch";
import { ListGroup } from "react-bootstrap";
import axios from "axios";
import { useEffect } from "react";

export const Cart = () => {
  const [data, setData] = useState();

  useEffect(() => {
    async function fetch() {
      try {
        let data = await axios.post(
          "http://localhost:5000/myTickets",
          { user: localStorage.getItem("user") },
          {
            headers: {
              Authorization: "Bearer " + localStorage.getItem("jwt"),
            },
          }
        );
        console.log(data.data);
        await setData(data.data);
      } catch (e) {
        console.log(e);
      }
    }
    fetch();
  }, []);

  return (
    <div>
      <ListGroup>
        {data?.map((e) => (
          <ListGroup.Item key={e._id}>
            <div className="d-flex justify-content-between">
              <div className="d-flex flex-column">
                <h3>Heim: {e.home_team}</h3>
                <h3>Ausw√§rts: {e.away_team}</h3>
              </div>
              <div className="d-flex flex-column">
                <h3>{e.date.split("T")[0]}</h3>
                <h3>{e.stadium}</h3>
              </div>
              <div className="d-flex flex-column">
                <h4>Block: {e.tickets.Block}</h4>
                <h4>Reihe: {e.tickets.line}</h4>
                <h4>Sitzplatz: {e.tickets.seat}</h4>
              </div>
            </div>
          </ListGroup.Item>
        ))}
      </ListGroup>
    </div>
  );
};
